class Solution {
public:
    int minimumTotal(vector<vector<int>>& t) {
        vector<vector<int>>dp;
        dp.push_back({t[0][0]});
        if(t.size()==1)
        return t[0][0];
        int mn=INT_MAX;
        for(int i=1;i<t.size();i++){
            vector<int>d;
            for(int j=0;j<t[i].size();j++){
                int g=t[i][j];
                if(j==0)g+=dp[i-1][j];
                else if(j==t[i].size()-1)g+=dp[i-1][j-1];
                else {
                    g += min(dp[i-1][j-1],dp[i-1][j]);
                }
                if(i==t.size()-1)mn=min(mn,g);
                d.push_back(g);
            }
            dp.push_back(d);
        }
         return mn;
    }
};
