class Solution {
    int helper(vector<vector<int>>& triangle, int i, int j, vector<vector<int>>& dp){
        int n = triangle.size() ;
        for(int j = 0; j < n; j++ ){
            dp[n-1][j] = triangle[n-1][j] ; 
        }

        for(int i = n-2; i >= 0; i--){
            for(int j = i; j >= 0; j-- ){
                dp[i][j] = triangle[i][j] + min(dp[i+1][j] , dp[i+1][j+1]) ;
            }
        }

        return dp[0][0] ;
    }
public:
