class Solution {
    int helper(vector<vector<int>>& triangle, int i, int j){
         int n = triangle.size() ;
        vector<int> front(n, -1) , curr(n, -1) ;

        for(int j = 0; j < n; j++ ){
            front[j] = triangle[n-1][j] ; 
        }

        for(int i = n-2; i >= 0; i--){
            for(int j = i; j >= 0; j-- ){
                curr[j] = triangle[i][j] + min(front[j] , front[j+1]) ;
            }
            front = curr ;
        }

        return front[0] ;
    }
public:
    int minimumTotal(vector<vector<int>>& triangle) { 
        int n = triangle.size() ;

        return helper(triangle, 0, 0) ;
