class Solution {
    public int minimumTotal(List<List<Integer>> A) {
        // For storing the result 
        // in a 1-D array, and  
        // simultaneously updating  
        // the result. 
        int []memo = new int[A.size()]; 
        int n = A.size() - 1; 

        // For the bottom row 
        for (int i = 0; i < A.get(n).size(); i++)  
            memo[i] = A.get(n).get(i); 

        // Calculation of the  
        // remaining rows, in 
        // bottom up manner. 
        for (int i = A.size() - 2;i >= 0; i--)  
            for (int j = 0;j<A.get(i).size(); j++)  
                memo[j] = A.get(i).get(j) + (int)Math.min(memo[j],memo[j + 1]); 

        // return the  
        // top element 
        return memo[0]; 
    }
}
