use std::cmp::min;
use std::collections::HashMap;
impl Solution {
    pub fn minimum_total(mut A: VVI) -> i32 {
        let mut m = HashMap::<String, i32>::new();
        fn go(A: &mut VVI, m: &mut HashMap<String, i32>, i: usize, j: usize) -> i32 {
            if i == A.len() {
                return 0;
            }
            let k = format!("{},{}", i, j);
            if !m.contains_key(&k) {
                let L = go(A, m, i + 1, j);
                let R = go(A, m, i + 1, j + 1);
                m.insert(k.clone(), A[i][j] + min(L, R));
            }
            return *m.get(&k).unwrap();
        }
        return go(&mut A, &mut m, 0, 0);
    }
}
