class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    using fun = function<int(int, int)>;
    using Map = unordered_map<string, int>;
    int minimumTotal(VVI& A, Map m = {}) {
        auto key = [](auto i, auto j) {
            stringstream ss; ss << i << "," << j;
            return ss.str();
        };
        fun go = [&](auto i, auto j) {
            if (i == A.size())
                return 0;
            auto k = key(i, j);
            if (m.find(k) == m.end()) {
                auto L = go(i + 1, j),
                     R = go(i + 1, j + 1);
                m[k] = A[i][j] += min(L, R);
            }
            return m[k];
        };
        return go(0, 0);
    }
};
