class Solution {
    public int minimumTotal(List<List<Integer>> triangle){
        if(triangle.size() == 1)
            return triangle.get(0).get(0);     //base case 
        int[][] dp = new int[triangle.size()][triangle.size()];  // new 2d array 
        int result = Integer.MAX_VALUE; 
        for(int i=0;i<triangle.size();i++)
            for(int j=0;j<=i;j++)
                dp[i][j] = triangle.get(i).get(j);  // creating the triangle in the 2d array like stairs

        for(int i=1;i< dp.length;i++){ //staring off with 1th row because 0th row is already the minimum sum
            for(int j=0;j<=i;j++){
                if(j==0){ //j==0 means its the left boundary of the triangle and only to reach is from the upper boundary
                    dp[i][j] += dp[i-1][j];
                }else if(j==i){ //j==i means its the right boundary of the triangle and only to reach is from the upper boundary
                    dp[i][j] += dp[i-1][j-1];
                }else{ // now its not the boundary, so there are two possible ways to reach, we have to use the minimum one
                    dp[i][j] += Math.min(dp[i-1][j-1],dp[i-1][j]);
                }
                if(i==dp.length-1) // if we are at the last row or bottom we keep on updating the result
                    result = Math.min(result,dp[i][j]);
            }
        }
        return result;
    }
}
