class Solution {
public:
    int minimumTotal(vector<vector<int>>& triangle) {

        if (triangle.size() <= 0) { return 0; }

        vector<vector<int>> aSearch;

        vector<int> aRow0;
        aRow0.push_back(triangle[0][0]);
        aSearch.push_back(aRow0);

        for (int aDepth=1;aDepth<triangle.size();aDepth++) {
            int aDepth1 = aDepth - 1;
            vector<int> aRow;    
            for (int i=0;i<triangle[aDepth].size();i++) {
                if (i == 0) {
                    aRow.push_back(triangle[aDepth][0] + aSearch[aDepth1][0]);
                } else if (i == (triangle[aDepth].size() - 1)) {
                    aRow.push_back(triangle[aDepth].back() + aSearch[aDepth1].back());
                } else {
                    aRow.push_back(triangle[aDepth][i] + min( aSearch[aDepth1][i - 1], aSearch[aDepth1][i] ));
                }
            }
            aSearch.push_back(aRow);
        }

        //Now we pick the minimum element from our search grid...
        int aResult = aSearch[triangle.size()-1][0];
        for (int i=1;i<aSearch[triangle.size()-1].size();i++) {
            aResult = min(aResult, aSearch[triangle.size()-1][i]);
        }

        return aResult;
    }
};
