class Solution {
public:
  int minimumTotal_tabulation_spaceOptimized(vector<vector<int>>&triangle){
      //space complextiy O(n) and Auxiliary space is O(1)
       int n=triangle.size();
       vector<int>dp(n,1e9);
       dp[0]=triangle[0][0];
       int prev;
       for(int i=1;i<n;i++){
           prev=dp[0];
           dp[0]=prev+triangle[i][0];
           for(int j=1;j<=i;j++){
               int curr=dp[j];
               dp[j]=triangle[i][j]+min(dp[j],prev);
               prev=curr;
           }
       }
       int ans=1e9;
       for(int i=0;i<n;i++)ans=min(ans,dp[i]);
       return ans;
   }


    int minimumTotal_tabulation(vector<vector<int>>&triangle){
        //space complexity is O(n) and Auxiliary space is also O(n)
       int n=triangle.size();
       vector<int>dp(n,1e9);
       dp[0]=triangle[0][0];
       for(int i=1;i<n;i++){
           vector<int>prev=dp;//Auxiliary space taken by prev vector
           dp[0]=prev[0]+triangle[i][0];
           for(int j=1;j<=i;j++){
               dp[j]=triangle[i][j]+min(prev[j],prev[j-1]);
           }
       }
       int ans=1e9;
       for(int i=0;i<n;i++)ans=min(ans,dp[i]);
       return ans;
   }

    int minimumTotal(vector<vector<int>>& triangle) {
        return minimumTotal_tabulation_spaceOptimized(triangle);
    }

};
