class Solution {
public:

    int solve(int r, int c , int n , vector<vector<int>> &tri){
        if(r == n-1) return tri[r][c];

        int vertical = tri[r][c] + solve(r+1 ,  c , n , tri);
        int diagonal = tri[r][c] + solve(r+1 , c+1 , n , tri);

        return min(vertical , diagonal);

    }

    int minimumTotal(vector<vector<int>>& triangle) {
        int n = triangle.size();
        int res = solve(0 , 0 , n , triangle);
        return res;
    }
};
```
