class Solution {
public:

    int solve(int r, int c , int n , vector<vector<int>> &tri , vector<vector<int>> &memo){
        if(r == n-1) return memo[r][c] = tri[r][c];
        if(memo[r][c] != -1) return memo[r][c];

        int vertical = tri[r][c] + solve(r+1 ,  c , n , tri , memo);
        int diagonal = tri[r][c] + solve(r+1 , c+1 , n , tri , memo);

        return memo[r][c] = min(vertical , diagonal);

    }

    int minimumTotal(vector<vector<int>>& triangle) {
        int n = triangle.size();
        vector<vector<int>> memo(n , vector<int> (n , -1));

        int res = solve(0 , 0 , n , triangle , memo);
        return memo[0][0];
    }
};
