class Solution {
public:
    //[ Recursion Solution with memoization ] --> bottom to top approach
    // Why DP ?  --> First try to understand at each cell --> there are 2 methods to reach bottom --> go down or go down&right 
    // try to observe that there are overlapping subproblems 
    // Now if we are at (i,j) --> then we need to calculate smallest possible path from (i+1,j) and (i+1,j+1) 
    // and for a cell (i,j+1) --> we need to calculate smallest possible path from (i+1,j+1) and (i+1,j+2) as subproblems 
    // Observe that we need to calculate (i+1,j+1) more than once --> need to memoize(store) the values once computed 

    //so , we take an external 2D matrix of size n*m where n--> number of rows in triangle given and m--> maximum number of col in triangle given 
    // dp[i][j] --> represents the smallest possible path from (i,j) to the bottom of the triangle 
    // optimal answer --> dp[0][0]

    int help(int row,int col,vector<vector<int>> &a,vector<vector<int>>&dp){
        if(row==a.size()) return 0; // base case : we need to stop once we exceed the base of the triangle given 
        if(dp[row][col]!=-1){       // if value is already calculated --> we use the precomputed value --> O(1) operation
            return dp[row][col];    
        }
        return dp[row][col]=a[row][col]+min(help(row+1,col,a,dp),help(row+1,col+1,a,dp));   // else we store and return the value computed via recursion 
        // 
    }

    int minimumTotal(vector<vector<int>>& a) {
        vector<vector<int>> dp(a.size(),vector<int>(a[a.size()-1].size(),-1));  // creating 2d array which will store all the calculated values for future
         // we are for sure of using the first cell in triangle and hence we take minimum of the 2 possible paths --> col=0 or col=1 
        return min(a[0][0]+help(1,0,a,dp),a[0][0]+help(1,1,a,dp));     
    }

    // Time Complexity : O(n*m) where n,m represents the dimensions of the triangles height and width 
    // Reason of Time Complexity : We reach each cell of triangle once --> total cells n*m/2 --> Time Complexity = O(n*m/2) ==O(n*m)
    // Space Complexity: O(n*m) because of 2D matrix
};
```
