class Solution {
public:
    // normal min value finding function
    long long int min(long long int a, long long int b) {
        return a > b? b : a; 
    }

    int minimumTotal(vector<vector<int>>& triangle) {
        // initializing dp
        vector<vector<long long int>> dp(triangle.size(), vector<long long int>(triangle.size(), 0));

        // dp[i][j]: min sum till ith row 
        int len = 2;
        dp[0][0] = triangle[0][0];

        for(int i=1;i<triangle.size();i++) {
            for(int j=0;j<len;j++) {
                if(j == 0) {
                    // when we are at the start of the array we can only take the value above the current element
                    dp[i][j] = dp[i-1][j] + triangle[i][j];
                } else if(j == len-1) {
                    // when we are at then end of the array we can only take the value at the diagonally left element in the previous row
                    dp[i][j] = dp[i-1][j-1] + triangle[i][j];
                } else {
                    // when we are at the middle of the array we will consider min among diagonally left and above element in previous row
                    dp[i][j] = min(dp[i-1][j-1], dp[i-1][j]) + triangle[i][j];
                }
            }
            len++;
        }

        long long int minv = INT_MAX;
        for(int i=0;i<triangle.size();i++) {
            // now we will find the min in the last array which will be our ans as our motive in this question was to reach last array with min cost.
            minv = min(dp[triangle.size()-1][i], minv);
        }

        return minv;
    }
};
```

