solutions.content:
15ms runtime
1) Scan through grid
2) Union to previous neighbors(r-1 or c-1) if they are 1
3) Keep track of all zeros.
4) Loop through set of zeros & find the area possible if that zero is converted to one.
5) PROFIT
```
class UF
{
    vector<int> p;
    vector<int> rank; // size of island

    public:
    UF(int n)
    {
        p=vector<int>(n*n,0);
        rank=vector<int>(n*n,0);
    }

    void makeSet(int i)
    {
        p[i]=i;
        rank[i]=1;
    }

    int findSet(int i)
    {
        while(i!=p[i])
        {
            p[i]=p[p[i]];
            i=p[i];
        }

        return i;
    }

    void Union(int a, int b)
    {
        int roota=findSet(a);
        int rootb=findSet(b);

        if(roota==rootb) return;

        if(rank[roota]>=rank[rootb])
        {
            p[rootb]=roota;
            rank[roota]+=rank[rootb];
        }
        else if(rank[roota]<rank[rootb])
        {
            p[roota]=rootb;
            rank[rootb]+=rank[roota];
        }
    }

    int getSize(int i)
    {
        int rooti=findSet(i);

        return rank[rooti];
    }
};
```

```
class Solution {

    int getPos(int r, int c, int C)
    {
        return (r*C)+c;
    }

    void getRC(int pos, int N, int& r, int& c)
    {
        r=pos/N;
        c=pos%N;
    }
public:
    int largestIsland(vector<vector<int>>& grid) {

        int n=grid.size();

        UF uf(n); // n*n

        vector<int> zeros;

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                int curr_pos=getPos(i,j,n);

                if(grid[i][j]==1)
                {
                    uf.makeSet(curr_pos);
                    if(i>0 && grid[i-1][j]==1)
                    {
                        int prev_pos=getPos(i-1,j,n);
                        uf.Union(curr_pos,prev_pos);
                    }

                    if(j>0 && grid[i][j-1]==1)
                    {
                        int prev_pos=getPos(i,j-1,n);
                        uf.Union(curr_pos,prev_pos);
                    }
                }
                else
                {
                    zeros.push_back(curr_pos);
                }
            }
        }

        int res=0;
        for(int i=0;i<zeros.size();i++)
        {
            unordered_set<int> nei;

            int r,c;
            getRC(zeros[i],n,r,c);

            if(r>0 && grid[r-1][c]==1)
            {
                int rootb=uf.findSet(getPos(r-1,c,n));
                nei.insert(rootb);
            }

            if(r<n-1 && grid[r+1][c]==1)
            {
                int rootb=uf.findSet(getPos(r+1,c,n));
                nei.insert(rootb);
            }

            if(c>0 && grid[r][c-1]==1)
            {
                int rootb=uf.findSet(getPos(r,c-1,n));
                nei.insert(rootb);
            }

            if(c<n-1 && grid[r][c+1]==1)
            {
                int rootb=uf.findSet(getPos(r,c+1,n));
                nei.insert(rootb);
            }

            int curr_area=0;
            for(auto& root: nei)
            {
                curr_area+=uf.getSize(root);
            }

            res=max(res,curr_area+1);
        }

        return (res==0)?n*n:res;
    }
};
```

