class Solution {
public:
    vector<pair<int,int>>dir={{1,0},{0,1},{-1,0},{0,-1}};
    bool check(int i,int j,vector<vector<int>>& grid){
        if(i<grid.size()&&j<grid[0].size()&&i>=0&&j>=0&&grid[i][j]==1)return true;
        return false;
    }
    int dfs(vector<vector<int>>&grid,int i,int j,int&val,vector<vector<int>>&vis){
        int count=1;
        vis[i][j]=val;
        for(auto x:dir){
            if(check(i+x.first,j+x.second,grid)&&vis[i+x.first][j+x.second]!=val){
               count += dfs(grid,i+x.first,j+x.second,val,vis); 
            }
        }
        return count;
    }
    int largestIsland(vector<vector<int>>& grid) {
        int val=1;
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<int>>vis(n+1,vector<int>(m+1));
        vector<int>dp(n*m+2);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==1&&vis[i][j]==0){
                    dp[val]=dfs(grid,i,j,val,vis);
                    val++;
                }
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]==1){
                    ans=max(ans,dp[vis[i][j]]);
                }else{
                    int u=0,d=0,l=0,r=0,ui=0,di=0,li=0,ri=0;
                    if(i>0){
                        ui=vis[i-1][j];
                        u=dp[vis[i-1][j]];
                    }
                    if(i<n-1){
                        di=vis[i+1][j];
                        if(ui!=di)d=dp[vis[i+1][j]];
                    }
                    if(j>0){
                        li=vis[i][j-1];
                        if(li!=ui&&li!=di)l=dp[vis[i][j-1]];
                    }
                    if(j<m){
                        ri=vis[i][j+1];
                        if(ri!=li&&ri!=ui&&ri!=di)r=dp[vis[i][j+1]];
                    }
                    ans=max(ans,r+l+u+d+1);
                }
            }
        }
        return ans;
    }
};
```

