Save the union size in `size[]`

```
class Solution {
    class DSU {
        int[] parent;
        int[] rank;
        int[] size;
        DSU (int n) {
            parent = new int[n];
            rank = new int[n];
            size = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
                size[i] = 1;
            }
        }
        int find(int x) {
            if (parent[x] != x) {
                parent[x] = find(parent[x]);
            }
            return parent[x];
        }
        void union(int x, int y) {
            int rx = find(x);
            int ry = find(y);
            if (rx == ry) {
                return;
            }
            if (rank[rx] == rank[ry]) {
                rank[rx]++;
                parent[ry] = rx;
                size[rx] += size[ry];
            } else if (rank[rx] < rank[ry]) {
                parent[rx] = ry;
                size[ry] += size[rx];
            } else {
                parent[ry] = rx;
                size[rx] += size[ry];
            }
        }
    }
    public int largestIsland(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        DSU dsu = new DSU(m * n);
        int[][] dirs = new int[][]{{-1, 0}, {0, -1}, {0, 1}, {1, 0}};
        int result = 1;
        // Build up the DSU, record the largest union.
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1) {
                    for (int d = 0; d < 2; d++) {
                        int row = i + dirs[d][0];
                        int col = j + dirs[d][1];
                        if (row >= 0 && col >= 0 && grid[row][col] == 1) {
                            dsu.union(i * n + j, row * n + col);
                            result = Math.max(result, dsu.size[dsu.find(i * n + j)]);
                        }
                    }
                }
            }
        }
        // For each '0', calculate the area after changing.
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 0) {
                    Map<Integer, Integer> map = new HashMap<>();
                    for (int d = 0; d < 4; d++) {
                        int row = i + dirs[d][0];
                        int col = j + dirs[d][1];
                        if (row >= 0 && col >= 0 && row < m && col < n && grid[row][col] == 1) {
                            int parent = dsu.find(row * n + col);
                            map.put(parent, dsu.size[parent]);
                        }
                    }
                    int temp = 1;
                    for (int v : map.values()) {
                        temp += v;
                    }
                    result = Math.max(result, temp);
                }
            }
        }
        return result;
    }
}
