class Solution:
    def makeSet(self, a):
        # Initially, a new element is in its own set
        # its parent is itself, its rank is 0
        self.parent[a] = a
        self.rank[a] = 0
        self.size[a] = 1

    def find(self, a):
        # find the root representative of the set that a belongs to
        if (self.parent[a] != a):
            self.parent[a] = self.find(self.parent[a])
        return self.parent[a]

    def union(self, a, b):
        # union the set that includes a and  the set that includes b
        a_root = self.find(a)
        b_root = self.find(b)
        if a_root != b_root:
            if self.rank[a_root] < self.rank[b_root]:
                a_root, b_root = b_root, a_root
            self.parent[b_root] = a_root
            self.size[a_root] += self.size[b_root]
            if self.rank[a_root] == self.rank[b_root]:
                self.rank[b_root] += 1
            self.answer = max(self.answer, self.size[a_root])

    def largestIsland(self, grid: List[List[int]]) -> int:
        self.answer = 0
        self.parent, self.rank, self.size = dict(), dict(), dict()
        n, m = len(grid), len(grid[0])
        # make set for each cell of island, and find the largest size of island
        for x in range(n):
            for y in range(m):
                if grid[x][y] == 1:
                    self.makeSet((x, y)) # make set for a new island (x, y)
                    self.answer = max(self.answer, 1)
                    for dx, dy in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                        x1, y1 = x + dx, y + dy
                        if 0 <= x1 < n and 0 <= y1 < m and (x1, y1) in self.parent:
                            # union (x, y) and its neighbor (x1, y1)
                            self.union((x1, y1), (x, y))
        # change each 0 to 1, and find the largest size of island
        for x in range(n):
            for y in range(m):
                if grid[x][y] == 0:
                    root_set = set()
                    for dx, dy in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                        x1, y1 = x + dx, y + dy
                        if 0 <= x1 < n and 0 <= y1 < m and grid[x1][y1] == 1:
                            root = self.find((x1, y1))
                            root_set.add(root)
                    self.answer = max(self.answer, 1 + sum([self.size[root] for root in root_set]))
        return self.answer
