class Solution {
    /*
        Approach:

        Two iterations through the grid:
            In first iteration through the grid, use DFS to count and group separate islands together.
                * Using a map to map groupNumber -> sizeOfIsland
            In second iteration, look for any '0' and add the values of all the unique groups from 4 directions.

        Complexities:
            * Time: O(n^2) - We are iterating through the grid twice. Even in DFS, we are not DFS-ing on visited indices.
            * Space: O(n^2) - We are creating group array to account for visited and for groupings
    */

    private int[][] dirs = { {1, 0}, {-1, 0}, {0, 1}, {0, -1} };

    public int largestIsland(int[][] grid) {
        int len = grid.length;
        int largest = 0;

        int[][] group = new int[len][len];
        Map<Integer, Integer> groupMap = new HashMap<>();
        int groupNum = 1;


        for (int i = 0; i < len; i++) {
            for (int n = 0; n < len; n++) {
                // If group[i][n] > 0, it means it was already calculated and assigned a group, we can skip
                if (grid[i][n] == 1 && group[i][n] == 0) {
                    group[i][n] = groupNum;
                    int sizeOfIsland = dfs(grid, i, n, group, groupNum); // the size of this group
                    groupMap.put(groupNum, sizeOfIsland);
                    groupNum++;

                    largest = Integer.max(largest, sizeOfIsland);
                }
            }
        }

        for (int i = 0; i < len; i++) {
            for (int n = 0; n < len; n++) {
                if (grid[i][n] == 0) {
                    int count = 0;

                    Set<Integer> dup = new HashSet<>();
                    for (int[] dir : dirs) {
                        int x = i + dir[0];
                        int y = n + dir[1];
                        if (x >= 0 && y >= 0 && x < len && y < len && group[x][y] > 0 && !dup.contains(group[x][y])) {
                            count+= groupMap.get(group[x][y]);
                            dup.add(group[x][y]);
                        }
                    }

                    largest = Integer.max(largest, count+1); // we add 1 to include the current changed '0'
                }
            }
        }

        return largest;
    }

    private int dfs(int[][] grid, int i, int n, int[][] group, int groupNum) {
        int count = 0;

        for (int[] dir : dirs) {
            int x = i + dir[0];
            int y = n + dir[1];
            if (x >= 0 && y >= 0 && x < grid.length && y < grid.length && grid[x][y] == 1 && group[x][y] == 0) {
                group[x][y] = groupNum;
                count += dfs(grid, x, y, group, groupNum);
            }
        }

        return count + 1;
    }

}
