class Solution {
public:
    vector<pair<int,int>>parent;
    vector<int>csize;
    int find_set(int a){
        if(parent[a].first==a)
            return a;
        return parent[a].first = find_set(parent[a].first);
    }
    void union_set(int a,int b){
        a = find_set(a);
        b = find_set(b);
        if(a!=b){
            if(parent[a].second == parent[b].second)
                parent[a].second++;
            if(parent[a].second < parent[b].second)
                swap(a,b);
            parent[b].first = a;
            csize[a]+=csize[b];
        }
    }
    int largestIsland(vector<vector<int>>& grid) {
        int n=grid.size();

//         make set : provide unique identity to each cell i*n + j 
//         initialize cells having val 1 to size 1
        for(int i=0;i<n*n;i++){
//             first -> parent second->rank
            parent.push_back({i,0});
            csize.push_back(0);
            if(grid[i/n][i%n])
                csize[i]++;
        }

//         union set all the cells with adjacent 1 if any (4 direction)
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(i+1<n and grid[i][j] and grid[i+1][j])
                    union_set(i*n+j,(i+1)*n+j);
                if(i-1>=0 and grid[i][j] and grid[i-1][j])
                    union_set(i*n+j,(i-1)*n+j);
                if(j+1<n and grid[i][j] and grid[i][j+1])
                    union_set(i*n+j,i*n+j+1);
                if(j-1>=0 and grid[i][j] and grid[i][j-1])
                    union_set(i*n+j,i*n+j-1);
            }
        }

//        for each 0 valued cell, find adjacent cell's unique representative to add their size 
//         if 1 valued cell continue
//         take into consideration no 0 cell case
        unordered_set<int>dparent;
        int ans = 0;
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]){
                    ans = max(ans,csize[i*n+j]);
                    continue;
                }
                int cans = 1;
                dparent.clear();
                if(i+1<n and grid[i+1][j])
                    dparent.insert(find_set((i+1)*n+j));
                if(i-1>=0 and grid[i-1][j])
                     dparent.insert(find_set((i-1)*n+j));
                if(j+1<n and grid[i][j+1])
                      dparent.insert(find_set(i*n+j+1));
                if(j-1>=0 and grid[i][j-1])
                     dparent.insert(find_set(i*n+j-1));

                for(int par:dparent){
                    cans += csize[par];
                }
                ans = max(ans,cans);
            }
          }

            return ans;
        }
};
