class Solution {
    /*

    Task:
        1. Perform DFS on each 1 cell and paint it with a unique color >= 2.
            I. Increase color variable for each new 1 cell we visit
        2. We need a data structure to hold the color and it's area, dictionary does it!
            I. The initial key and value pair is 0 as a cell of 0 has 0 area
        3. As we finish searching for 1's in the grid, there are 2 cases
            I. Either the grid is filled with only 1's, therefore return rows*cols
            II. Or the grid is all zero's, return 1 as we can only flip one 0 cell

    Complexity:
        TC: O(R*C) where R = # of rows and C # of columns
        SC: O(R*C)

    */

    var rows = 0
    var cols = 0
    var grid: [[Int]] = []
    let directions: [[Int]] = [[1,0], [-1,0], [0,1], [0,-1]]

    func largestIsland(_ grid: [[Int]]) -> Int {

        self.rows = grid.count
        self.cols = grid[0].count
        self.grid = grid

        // Key = color and value is the area. A cell = 0 will have no area, 0.
        var map: [Int: Int] = [:]
        map[0] = 0

        var color = 2

        // Color All 1's
        for r in 0..<rows {
            for c in 0..<cols where self.grid[r][c] == 1 {
                let area = dfs(r,c,color)
                map[color] = area 
                color += 1
            }
        }

        // The grid could be all 0's and no cell was painted
        var result = map[2, default:0]

        // Optimization. No need to do extra work as we can flip only 1 cell
        if result == 0 { return 1 } 

        for r in 0..<rows {
            for c in 0..<cols where self.grid[r][c] == 0 {

                // Find colors that are around this cell 
                var uniqueColors: Set<Int> = []

                for direction in directions {
                    let nextRow = r + direction[0]
                    let nextCol = c + direction[1]

                    if isValid(nextRow, nextCol) {
                        let validColor = self.grid[nextRow][nextCol]
                        uniqueColors.insert(validColor)
                    }
                }

                var totalArea = 1 
                for color in uniqueColors {
                    totalArea += map[color]!
                }
                result = max(totalArea, result)
            }
        }
        return result
    }

    func dfs(_ r: Int, _ c: Int, _ color: Int) -> Int {
        grid[r][c] = color
        var totalArea = 1

        for direction in directions {
            let nextRow = r + direction[0]
            let nextCol = c + direction[1]

            // Only color 1's
            if isValid(nextRow,nextCol) && grid[nextRow][nextCol] == 1 {
                totalArea += dfs(nextRow,nextCol,color)
            }
        }
        return totalArea
    }

    func isValid(_ r: Int, _ c: Int) -> Bool {
        return r >= 0 && r < rows && c >= 0 && c < cols
    }
}
