public int largestIsland(int[][] grid) {

	int max_area = 1;
	int row = grid.length;
	int col = grid[0].length;
	int[] colors = new int[row * col + 2];
	boolean[][] visited = new boolean[row][col];
	int[][] dirs = { { 0, 1 }, { 1, 0 }, { 0, -1 }, { -1, 0 } };
	int index = 2;

	for (int i = 0; i < row; i++) {
		for (int j = 0; j < col; j++) {
			if (grid[i][j] == 1) {
				int area = dfsHelper(grid, visited, i, j, row, col, index, dirs);
				colors[index++] = area;
				max_area = Math.max(area, max_area);
			}
		}
	}

	for (int i = 0; i < row; i++) {
		for (int j = 0; j < col; j++) {
			if (grid[i][j] == 0) {
				int ans = 0;
				HashSet<Integer> set = new HashSet<Integer>();
				for (int[] dir : dirs) {
					int r = i + dir[0];
					int c = j + dir[1];
					if (r < 0 || c < 0 || r >= row || c >= col || grid[r][c] == 0)
						continue;
					set.add(grid[r][c]);
				}
				for (int node : set)
					ans += colors[node];
				ans += 1;
				max_area = Math.max(ans, max_area);
			}
		}
	}

	return max_area;
}

public int dfsHelper(int[][] grid, boolean[][] visited, int i, int j, int row, int col, int color, int[][] dirs) {

	visited[i][j] = true;
	grid[i][j] = color;
	int area = 1;
