                        int x=i+dx[k];
                        int y=j+dy[k];
                        if(x<0 || x>=n || y<0 || y>=n)continue;
                        if(grid[x][y]==1){
                            if(s.findUPar(n*x + y)!=s.findUPar(n*i + j)){
                                s.unionBySize(n*x + y, n*i + j);
                            }
                        }
                    }
                }
            }
        }
        int ans=0;
        for(int i=0;i<n*n;i++){
            ans=max(ans,s.size[i]);
        }





        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]==0){
                    int count=1;
                    vector<int>ultimate;
                    int dx[]={-1,0,1,0};
