class Solution {
    int count =0;
    int max=0;
    public int largestIsland(int[][] grid) {
        int c=0;
        for(int i[]: grid){
            for(int j: i){
                if(j==1){
                    c++;
                }
            }
        }
        if(c==0 ){
            return 1;// if all are zeroes
        }
        if(c== grid.length* grid.length){
            return c;//if all are ones
        }
        HashMap<Integer,Integer> hm = new HashMap<>();// storing components 
        int id=2;
        for(int i=0 ; i<grid.length; i++){
            for(int j=0; j<grid.length; j++){
                if(grid[i][j]==1){
                    count=0;
                    dfs(grid, i, j, id);
                    hm.put(id, count);
                    id++;
                }
            }
        }
        for(int i=0;i <grid.length; i++){
            for(int j=0; j<grid.length; j++){
                if(grid[i][j]==0){
                    int ansforthatzero = bfs(grid,i , j, hm);
                    if(ansforthatzero>max){
                        max=ansforthatzero;
                    }
                }
            }
        }
        return max;



    }
    public void dfs(int [][]grid, int i, int j, int id){
        if(i<0 || j<0 || i>=grid.length || j>= grid.length || grid[i][j]==0 || grid[i][j]==id){
            return;
        }
        count++;
        grid[i][j]=id;
        dfs(grid, i+1, j, id);
        dfs(grid, i, j-1, id);
        dfs(grid, i, j+1, id);
        dfs(grid, i-1, j, id);

    }
    public int  bfs(int [][]grid, int i, int j, HashMap<Integer,Integer> hm){
        int dir[][]= {{0,1}, {0,-1}, {1,0}, {-1,0}};
        HashSet<Integer> hs= new HashSet<>();
        for(int k=0;k<4 ; k++){
            int nx= dir[k][0]+i;
            int ny= dir[k][1]+j;
            if(nx>=0 && ny>=0 && nx<=grid.length-1 && ny<=grid.length-1 && grid[nx][ny]!=0){
