class Solution {
public:
    int dir[4][2]={{1,0},{0,1},{-1,0},{0,-1}};

//      for checking if the grid index is not out of bound.
    bool isValid(int i,int j,int n){
        if(i>=0 && j>=0 && i<n && j<n)return true;
        return false;
    }

    // making all adjacent one to the diff value.
    int dfs(int i,int j,vector<vector<int>> &grid,int num,int n){
        if(!isValid(i,j,n) || grid[i][j]!=1)return 0;

        int c=0;
        grid[i][j]=num;
        c+=dfs(i+1,j,grid,num,n);
        c+=dfs(i-1,j,grid,num,n);
        c+=dfs(i,j+1,grid,num,n);
        c+=dfs(i,j-1,grid,num,n);
        return 1+c;
    }

    // main fucntion
    int largestIsland(vector<vector<int>>& grid) {
        int n=grid.size();
