            for(int j=0;j<n;j++){
                if(grid[i][j]==1){
                    int count=dfs(i,j,grid,num,n);
                    find[num]=count;
                    num++;
                    ans=max(ans,count);
                }
            }
        }

        // looking for maximum one for every index of 0.
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
