class Solution {
public:
    bool is_safe(int i, int j, int n){
        return (i>=0 and j>=0 and i<n and j<n);
    }
    int dfs(vector<vector<int>>& g, vector<vector<int>> &vis, int i, int j, int clr){
        vis[i][j] = clr;

        int x[] = {-1, 1, 0, 0};
        int y[] = {0, 0, -1, 1};

        int c = 0;
        for(int d=0; d<4; d++){
            int nx = i+x[d], ny = j+y[d];
            if(is_safe(nx, ny, g.size()) and g[nx][ny]==1 and vis[nx][ny]!=clr){
                c += 1+dfs(g, vis, nx, ny, clr);
            }
        }
        return c;
    }
    int largestIsland(vector<vector<int>>& grid) {
        int n = grid.size();
        vector<vector<int>> vis(n, vector<int>(n, 0));
        int cnt = 2;
        unordered_map<int, int> island_size;

        int x[] = {-1, 1, 0, 0};
        int y[] = {0, 0, -1, 1};

        for(int i=0; i<n; i++){
            for(int j=0; j<n; j++){
                if(grid[i][j] and !vis[i][j]){
                    int size = 1+dfs(grid, vis, i, j, cnt);
                    island_size[cnt] = size;
                    cnt++;
                }
            }
        }

        int big = 0;
        for(int i=0; i<n; i++){
            for(int j=0; j<n; j++){
                if(grid[i][j]==0){
                    int temp = 1;
                    unordered_set<int> seen;
                    for(int d=0; d<4; d++){
                        if(is_safe(i+x[d], j+y[d], grid.size()) and vis[i+x[d]][j+y[d]]){
                            int clr = vis[i+x[d]][j+y[d]];
                            if(seen.find(clr)==seen.end()){
                                temp += island_size[clr];
                                seen.insert(clr);
                            }
                        }
                    }
                    big = max(big, temp);
                }
            }
        }
        return big==0?n*n:big;
    }
};
