```
class Solution {
public:
    int dfs(int r, int c, vector<vector<int>> &grid, int &mark){      
        if(r < 0 || c < 0 || r >= grid.size() || c >= grid[0].size()) return 0;
        if(grid[r][c] == 0 || grid[r][c] ==  mark)  return 0;

        grid[r][c] = mark;
        int count = 1;
        count += dfs(r + 1, c, grid,mark);
        count += dfs(r - 1, c , grid, mark);
        count += dfs(r, c + 1, grid, mark);
        count += dfs(r, c - 1, grid,  mark);
        return count;
    }


    int largestIsland(vector<vector<int>>& grid) {
        int result = 0;
        int mark = 2;
        unordered_map<int,int> islands_area;

         for(int i = 0; i < grid.size(); i++){
            for(int j = 0; j < grid[i].size(); j++){
                if(grid[i][j] == 1){
                    int count = dfs(i, j, grid, mark);

                    if(count != 0){

                        islands_area[mark] = count;
                        mark++;
                    }

                }
            }
        }

        for(int i = 0; i < grid.size(); i++){
            for(int j = 0; j < grid.size(); j++){
                if(grid[i][j] == 0){
                    set<int> unique_islands;
                    if(i  -  1 >= 0 && grid[i-1][j] != 0) unique_islands.insert(grid[i- 1][j]);
                    if(i  +  1 < grid.size() && grid[i + 1][j] != 0) unique_islands.insert(grid[i+ 1][j]);
                    if(j  -  1 >= 0 && grid[i][j - 1] != 0) unique_islands.insert(grid[i][j - 1]);
                    if(j  +  1 < grid[0].size() && grid[i][j + 1] != 0) unique_islands.insert(grid[i][j + 1]);
                    int sum = 1;
                    for(auto i : unique_islands) sum += islands_area[i];
                    result = max(result, sum);
                }
            }
        }



        return result == 0 ? grid.size() * grid[0].size() : result;
    }
};
