    private int[][] dirs = {{0, -1}, {-1, 0}, {0, 1}, {1, 0}};
    Map<Integer, Integer> map = new HashMap<>();  //map is used to store <group number -> area>
    private int[][] group;

    public int largestIsland(int[][] grid) {
        if (grid == null || grid.length == 0)
            return 0;
        int m = grid.length, n = grid[0].length;
        group = new int[m][n];

        makeIslandGroups(grid, m, n);  //DFS the whole graph to group the island and store each island area into the map

        int res = connectGroups(grid, m, n); //Scan the graph, if there is any 0, connect the 0 point with its surrounding groups

        if (grid[0][0] == 1 && res == 0) //if any point is 1, but result is 0, mean all points in the graph is 1
            return m * n;
        return res;
    }

    private void makeIslandGroups(int[][] grid, int m, int n) {
        int groupNum = 1;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1) {
                    map.put(groupNum, dfs(grid, i, j, m, n, groupNum++));
                }

            }
        }
    }

    private int connectGroups(int[][] grid, int m, int n) {
        int max = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 0) {
                    int area = 1;
                    Set<Integer> set = new HashSet<>();
                    for (int[] dir : dirs) {
                        int x = i + dir[0], y = j + dir[1];
                        if (x >= 0 && x < m && y >= 0 && y < n && group[x][y] > 0) {
                            if (!set.contains(group[x][y])) {  //the surrounding groups may be duplicated, but we need to only add once
                                area += map.get(group[x][y]);
                                set.add(group[x][y]);
                            }
                        }
                    }
                    max = Math.max(max, area);
                }
            }
        }
        return max;
    }

    private int dfs(int[][] grid, int i, int j, int m, int n, int groupNum) {
        if (i < 0 || i >= m || j < 0 || j >= n || grid[i][j] == 0 || group[i][j] > 0)
            return 0;
        group[i][j] = groupNum;
        int res = 1;
        for (int[] dir : dirs) {
            res += dfs(grid, i + dir[0], j + dir[1], m, n, groupNum);
        }
        return res;
    }

