```class Solution {
public:
    bool isValid(int r,int c,int rows,int cols)
    {
        return r>=0 && r<rows && c>=0 && c<cols;
    }
    void dfs(int row,int col,int rows,int cols,vector<vector<int>>& grid,int id,int& area)
    {
        if(!isValid(row,col,rows,cols) || grid[row][col]==id || grid[row][col]==0)
            return;
        grid[row][col]=id;
        area++;
        dfs(row+1,col,rows,cols,grid,id,area);
        dfs(row-1,col,rows,cols,grid,id,area);
        dfs(row,col+1,rows,cols,grid,id,area);
        dfs(row,col-1,rows,cols,grid,id,area);
    }
    int largestIsland(vector<vector<int>>& grid) {
        int rows=grid.size(),cols=grid[0].size(),id=2,maxArea=0,maxMergeArea=0;
        map<int,int> gridArea;
        for(int i=0;i<rows;i++)
        {
            for(int j=0;j<cols;j++)
            {
                if(grid[i][j]==1)
                {
                    int area=0;
                    dfs(i,j,rows,cols,grid,id,area);             // Run DFS and update the connected 1's with id and keep track of area in map-> gridArea
                    gridArea[id]=area;
                    id++;
                    maxArea=max(maxArea,area);
                }
            }
        }
		//Now we have the second image, grid which has ID and map which has area. We can traverse the grid and for each 0, we will find the neighbor with unique gridID and calculate the area that can we formed with merging all unique grids surrounding 0.
        for(int i=0;i<rows;i++)
        {
            for(int j=0;j<cols;j++)
            {
                if(grid[i][j]==0)
                {
                    int mergeArea=1;      // Initial area is 1 because we are reverting 0 to 1, Now check all 4 directions and add unique Grid's area.
                    map<int,int> m;
                    if(i-1>=0 && grid[i-1][j]!=0)
                    {
                        mergeArea+=gridArea[grid[i-1][j]];
                        m[grid[i-1][j]]++;
                    }
                    if(i+1<rows && grid[i+1][j]!=0)
                    {
                        if(m[grid[i+1][j]]==0)
                        {
                            mergeArea+=gridArea[grid[i+1][j]];
                            m[grid[i+1][j]]++;
                        }
                    }
                    if(j-1>=0 && grid[i][j-1]!=0)
                    {
                        if(m[grid[i][j-1]]==0)
                        {
                            mergeArea+=gridArea[grid[i][j-1]];
                            m[grid[i][j-1]]++;
                        }
                    }
                    if(j+1<cols && grid[i][j+1]!=0)
                    {
                        if(m[grid[i][j+1]]==0)
                        {
                            mergeArea+=gridArea[grid[i][j+1]];
                            m[grid[i][j+1]]++;
                        }
                    }
                    maxMergeArea=max(maxMergeArea,mergeArea);
                }
            }
        }
        return max(maxArea,maxMergeArea);
    }
};``

