class Solution {
    /*
      Thoughts:

      Unvisited islands maintain the value of 1. Once an island is visited recursively
      through a depth first search, its index is overwritten with a unique island tag.
      Depth first search will also compute size of island, so that the island's tag
      is associated with its size.

      Once the array has been modified to have each island distinguished by a different
      numeric tag, must compute the square which is 0 and has the largest neighbors.

      Edge case - no islands: return 1 as filling in a square results in an island of 1
      Edge case - one big island: return the size of that big island
      Main occurence - size of that 0's neighbors added together, plus 1,
                        as that 0 is the land bridge to connect the islands

    */
    private HashMap<Integer,Integer> islandSizes;
    private int n;

    public int largestIsland(int[][] grid) {
      n = grid.length;
      islandSizes = new HashMap<>();

      int islandTag = 2;
        for(int row = 0; row < n; row++)
          for(int col = 0; col < n; col++)
            if(grid[row][col] == 1)
              islandSizes.put(islandTag, sizeOfIsland(row,col,grid, islandTag++));

        int max = 1;
        for(Integer i : islandSizes.values())
          max = Math.max(max,i);
        for(int row = 0; row < n; row++)
          for(int col = 0; col < n; col++)
            if(grid[row][col] == 0)
              max = Math.max(max, maxNeighborValue(row,col, grid));

        return max;
    }
    /**
	Overwrites the island and its connections with islandTag, returns the total size of the
	connected component (i.e. island)
	*/
    private int sizeOfIsland(int row, int col, int[][] grid, int islandTag){
      if(row >= n || col >= n || col < 0 || row < 0)
        return 0;
      else if (grid[row][col] != 1)
        return 0;
      else {
        grid[row][col] = islandTag;
        return 1 + sizeOfIsland(row+1,col,grid, islandTag) +
                   sizeOfIsland(row-1,col,grid, islandTag) +
                   sizeOfIsland(row,col+1,grid, islandTag) +
                   sizeOfIsland(row,col-1,grid, islandTag);
      }
    }

   /**
   Computes the size of the connected component (e.g. island) formed by turning this coordinate
   into land
   */
    private int maxNeighborValue(int row, int col, int[][] grid){
      Set<Integer> neighbors = new HashSet<>();
      if(col + 1 < n)
        neighbors.add(grid[row][col+1]);
      if(col - 1 >= 0)
        neighbors.add(grid[row][col-1]);
      if(row + 1 < n)
        neighbors.add(grid[row+1][col]);
      if(row - 1 >= 0 )
        neighbors.add(grid[row-1][col]);

      int sum = 1;
      for(Integer neighbor : neighbors)
        sum += islandSizes.getOrDefault(neighbor,0);
      return sum;
    }
}
