        for(int i = 0; i < m; i++)
            for(int j = 0; j < n; j++)
            {
                var set = new HashSet<int>();  
                if(grid[i][j] == 0)
                {                    
                    for(int k = 0; k < 4; k++)
                    {
                        var row = i + dx[k];
                        var col = j + dy[k];

                        if(row >= 0 && row < grid.Length && col >= 0 && col < grid[0].Length && grid[row][col] > 1 && !set.Contains(grid[row][col])) //gothca
                            set.Add(grid[row][col]);
                    }                    
                }
                var curMax = 0;
                foreach(var id in set)
                {
                    curMax += map[id];
                }
                max = Math.Max(max, 1 + curMax);
            }

        return max;        
