class Solution:
    def largestIsland(self, grid: List[List[int]]) -> int:
        N = len(grid)

        self.tree = {}
        self.area = collections.defaultdict(int)
        zero_cells = set()

        for i in range(N):
            for j in range(N):
                if grid[i][j] == 1:
                    self.setRoot(i, j)
                    for dx, dy in ((-1, 0), (0, -1), (1, 0), (0, 1)):
                        nx, ny = i + dx, j + dy
                        if 0 <= nx < N and 0 <= ny < N and (nx, ny) in self.tree: 
                            self.union((i, j), (nx, ny))                                             
                else:
                    zero_cells.add((i,j))

        for key in self.tree:
            root = self.findRoot(key)
            self.area[root] += 1

        res = max(self.area.values()) if self.area.values() else 0
        for i, j in zero_cells:
            nei = set()
            for dx, dy in ((-1, 0), (1, 0), (0, -1), (0, 1)):
                nx, ny = i + dx, j + dy
                if 0 <= nx < N and 0 <= ny < N and grid[nx][ny] == 1:   
                    nei.add(self.findRoot((nx, ny)))
            res = max(res, 1 + sum(self.area[x] for x in nei))
        return res       

    def union(self, a, b):
        roota = self.findRoot(a)
        rootb = self.findRoot(b)

        if roota != rootb:
            self.tree[roota] = rootb

    def setRoot(self, x, y):
        if (x, y) not in self.tree:
            self.tree[(x,y)] = (x,y)

    def findRoot(self, a):
        if a != self.tree[a]:
            self.tree[a] = self.findRoot(self.tree[a])
        return self.tree[a]
