class DSU(val N: Int) {
    val parent = IntArray(N)
    val size = IntArray(N)
    var maxSize = 1

    init {
        makeSet()
    }

    private fun makeSet() {
        for (i in 0 until N) {
            parent[i] = i
            size[i] = 1
        }
    }

    fun find(x: Int): Int {
        if (parent[x] != x) {
            parent[x] = find(parent[x])
        }
        return parent[x]
    }

    fun union(x: Int, y: Int): Boolean {
        val (xP, yP) = find(x) to find(y)
        if (xP == yP) return false
        if (size[xP] > size[yP]) {
            size[xP] += size[yP]
            parent[yP] = xP
            maxSize = maxOf(maxSize, size[xP])
        } else {
            size[yP] += size[xP]
            parent[xP] = yP
            maxSize = maxOf(maxSize, size[yP])
        }
        return true
    }
}
