class Solution {
	private int r, c; 
	private int color = 2;
	private int maxArea = 0;
	private int[][] directions = {{0,1},{0,-1},{1,0},{-1,0}}; //easy to traverse 4 directions
	private Map<Integer, Integer> map = new HashMap<>(); //<color, area>;
	public int largestIsland(int[][] grid) {

		r = grid.length;
		c = grid[0].length;
		//corner case
		if(grid == null || r== 0 || c == 0) return 0;

		//find each island area, to mark diffrent color 
		for(int i = 0; i< r; i++){
			for(int j = 0; j<c; j++){
				if(grid[i][j] == 1){
					bfs(grid, i, j, color);
					color++;
				}
			}
		}

		for(int i = 0; i<r; i++){
			for(int j = 0; j < c; j++){
				if(grid[i][j] == 0){
					Set<Integer> s = new HashSet<>();//record how many colors around 4 directions
					int area = 1;

					for(int []d: directions){
						int nx = i + d[0];
						int ny = j + d[1];
						if(nx >= 0 && nx < r && ny >= 0 && ny < c && !s.contains(grid[nx][ny]) && grid[nx][ny] != 0){
							s.add(grid[nx][ny]);
							area += map.get(grid[nx][ny]);
						}
					}
					maxArea = Math.max(maxArea, area);
				}
			}
		}
		return maxArea;

	}

	private void bfs(int[][]grid, int x, int y, int color){

		Queue<int[]> queue = new LinkedList<>();
		int area = 0;
		queue.offer(new int[]{x, y});
		grid[x][y] = color;

		while(!queue.isEmpty()){

			int[] start = queue.poll();

			for(int[]d: directions){

				int nx = start[0] + d[0];
				int ny = start[1] + d[1];

				if(nx >= 0 && nx < r && ny >= 0 && ny < c && grid[nx][ny] == 1){

					queue.offer(new int[]{nx, ny});
					grid[nx][ny] = color;
				}
			}
			area++;
			map.put(color, area);
			maxArea = Math.max(maxArea, area);
		}
	}
}

