class Solution {
    class DSU {
        int[] parent;
        int[] size;

        public DSU(int size) {
            this.size = new int[size];
            parent = new int[size];
            for (int i = 0; i < size; i++) {
                parent[i] = i;
                this.size[i] = 1;
            }
        }

        public int find(int x) {
            if (parent[x] != x)
                parent[x] = find(parent[x]);
            return parent[x];
        }

        // Union By Size

        public boolean union(int x, int y) {
            int xr = find(x), yr = find(y);
            if (xr == yr) {
                return false;
            } else if (size[xr] < size[yr]) {
                parent[xr] = yr;
                size[yr] += size[xr];
            }
           else {
                parent[yr] = xr;
                size[xr] += size[yr];
            }
            return true;
        }
    }

