public class Solution 
{
    public int LargestIsland(int[][] grid)
    {
        var islands = new Dictionary<int, int> {{0, 0}};
        var totalIslandsVolume = 0;
        for (int i = 0; i < grid.Length; i++)
        {
            for (int j = 0; j < grid[i].Length; j++)
            {
                if (grid[i][j] == 0 || grid[i][j] > 1) continue;
                int colorId = islands.Count + 1;
                int islandVolume = FloodFillIsland(grid, i, j, colorId);
                totalIslandsVolume += islandVolume;
                islands.Add(colorId, islandVolume);
            }
        }

        if (totalIslandsVolume == grid.Length * grid[0].Length) 
            return totalIslandsVolume;

        int max = 1;
        var uniqueIds = new HashSet<int>();
        for (int i = 0; i < grid.Length; i++)
        {
            for (int j = 0; j < grid[i].Length; j++)
            {
                if(grid[i][j] != 0) continue;

                uniqueIds.Add(GetIslandId(grid, i + 1, j));
                uniqueIds.Add(GetIslandId(grid, i - 1, j));
                uniqueIds.Add(GetIslandId(grid, i, j + 1));
                uniqueIds.Add(GetIslandId(grid, i, j - 1));

                var currMax = uniqueIds.Sum(id => islands[id]) + 1;
                max = Math.Max(currMax, max);
                uniqueIds.Clear();
            }
        }
        return max;


        int FloodFillIsland(int[][] mtx, int i, int j, int color)
        {
            if (i < 0 || i >= mtx.Length || j < 0 || j >= mtx[i].Length)
                return 0;

            if (mtx[i][j] == 0 || mtx[i][j] == color)
                return 0;

            mtx[i][j] = color;

            return 1 + FloodFillIsland(mtx, i + 1, j, color)
                     + FloodFillIsland(mtx, i - 1, j, color)
                     + FloodFillIsland(mtx, i, j + 1, color)
                     + FloodFillIsland(mtx, i, j - 1, color);
        }

        int GetIslandId(int[][] mtx, int i, int j)
        {
            if (i < 0 || i >= mtx.Length || j < 0 || j >= mtx[i].Length)
                return 0;

            return mtx[i][j];
        }
    }
}
