class Solution {
    public int largestIsland(int[][] grid) {
        int n=grid.length;
        DisjointSet ds=new DisjointSet(n*n);
        for(int i=0;i<n;i++) { //clustering islands
            for(int j=0;j<n;j++) {
                if(grid[i][j]==1) {
                    if(i-1>=0 && grid[i-1][j]==1) {
                        ds.union(getIndex(i, j, n), getIndex(i-1, j, n));
                    }
                    if(j-1>=0 && grid[i][j-1]==1) {
                        ds.union(getIndex(i, j, n), getIndex(i, j-1, n));
                    }
                }
            }
        }

        int ans=Integer.MIN_VALUE;
        for(int x: ds.sets) {
            if(x<0) {
                ans=Math.max(ans, -x);
            }
        }

        //check if any 0 is made 1 then any bridge is formed between islands
        for(int i=0;i<n;i++) { 
            for(int j=0;j<n;j++) {
                if(grid[i][j]==0) {
                    int size=1;
                    int p1, p2, p3, p4;
                    p1=p2=p3=p4=-1;
                    if(i-1>=0 && grid[i-1][j]==1) { //up
                        p1=ds.find(getIndex(i-1, j, n));
                        size+=-ds.sets[p1];
                    }
                    if(i+1<n && grid[i+1][j]==1) { //down
                        p2=ds.find(getIndex(i+1, j, n));
                        if(p1!=p2) {
                            size+=-ds.sets[p2];
                        }
                    }
                    if(j-1>=0 && grid[i][j-1]==1) { //left
                        p3=ds.find(getIndex(i, j-1, n));
                        if(p1!=p3 & p2!=p3) {
                            size+=-ds.sets[p3];
                        }
                    }
                    if(j+1<n && grid[i][j+1]==1) { //right
                        p4=ds.find(getIndex(i, j+1, n));
                        if(p1!=p4 & p2!=p4 && p3!=p4) {
                            size+=-ds.sets[p4];
                        }
                    }
                    ans=Math.max(ans, size);
                }
            }
        }
        return ans;
    }

    private int getIndex(int i, int j, int N) {
        return i*N+j;
    }

    private class DisjointSet {
        int sets[];
        DisjointSet(int size) {
            sets=new int[size];
            Arrays.fill(sets, -1);
        }

        public int find(int idx) {
            if(sets[idx]<0) return idx;
            else return find(sets[idx]);
        }

        public int union(int i1, int i2) {
            int p1=find(i1);
            int p2=find(i2);
            if(p1==p2) {
                return sets[p1];
            }else {
                int w1=-sets[p1];
                int w2=-sets[p2];
                if(w1>=w2) {
                    sets[p2]=p1;
                    return sets[p1]=-(w1+w2);
                }else {
                    sets[p1]=p2;
                    return sets[p2]=-(w1+w2);
                }
            }
        }
    }
}
