class DisjointSet 
{    
public: 
vector<int> rank, parent, size; 
    DisjointSet(int n) 
{
        rank.resize(n+1, 0); 
        parent.resize(n+1);
        size.resize(n+1); 
        for(int i = 0;i<=n;i++) {
            parent[i] = i; 
            size[i] = 1; 
        }
    }

    int findUPar(int node) 
   {
        if(node == parent[node])
            return node; 
        return parent[node] = findUPar(parent[node]); 
    }

    void unionByRank(int u, int v) 
 {
        int ulp_u = findUPar(u); 
        int ulp_v = findUPar(v); 
        if(ulp_u == ulp_v) return; 
        if(rank[ulp_u] < rank[ulp_v]) {
            parent[ulp_u] = ulp_v; 
        }
        else if(rank[ulp_v] < rank[ulp_u]) {
            parent[ulp_v] = ulp_u; 
        }
        else {
            parent[ulp_v] = ulp_u; 
            rank[ulp_u]++; 
        }
    }

    void unionBySize(int u, int v)
 {
        int ulp_u = findUPar(u); 
        int ulp_v = findUPar(v); 
        if(ulp_u == ulp_v) return; 
        if(size[ulp_u] < size[ulp_v]) {
            parent[ulp_u] = ulp_v; 
            size[ulp_v] += size[ulp_u]; 
        }
        else {
            parent[ulp_v] = ulp_u;
            size[ulp_u] += size[ulp_v]; 
        }
    }
}; 
class Solution {

public:
    int findnode(int row,int col,int s)
    {
        return (row*s)+col;
    }
    int largestIsland(vector<vector<int>>& grid) 
    {

        int s=grid.size();
        DisjointSet ds(s*s);

        int drow[]={-1,0,1,0};
        int dcol[]={0,1,0,-1};

        int nonconverted=0;//store the maximum nodes if none of them are converted

        vector<vector<int>>visited(s,vector<int>(s,0));

        for(int row=0;row<s;row++)
        {
            for(int col=0;col<s;col++)
            {
                if(grid[row][col]==1 && visited[row][col]!=1)
                {
                    visited[row][col]=1;
                    for(int i=0;i<4;i++)
                    {
                        int nrow=row+drow[i];
                        int ncol=col+dcol[i];

                        if(nrow>=0 && nrow<s && ncol>=0 && ncol<s && grid[nrow][ncol]==1 )
                        {
                            int nodeno=findnode(row,col,s);
                            int adj_nodeno=findnode(nrow,ncol,s);
                            ds.unionBySize(nodeno,adj_nodeno);
                            nonconverted=max(nonconverted,ds.size[ds.findUPar(nodeno)]);
                        }
                    }
                }
            }
        }
        int converted=0;
        for(int row=0;row<s;row++)
        {
            for(int col=0;col<s;col++)
            {
                if(grid[row][col]==0)
                {
                    set<int>st;
                    for(int i=0;i<4;i++)
                    {
                        int nrow=row+drow[i];
                        int ncol=col+dcol[i];
                        if(nrow>=0 && nrow<s && ncol>=0 && ncol<s && grid[nrow][ncol]==1)
                        {
                            int adj_nodeno=findnode(nrow,ncol,s);
                            int par=ds.findUPar(adj_nodeno);
                            st.insert(par);
                        }
                    }
                    int temp=0;
                    for(auto it:st)
                        temp+=ds.size[it];
                    converted=max(converted,temp);
                }
            }
        }
        return max(converted+1,nonconverted);

    }
};
