def largestIsland(self, grid: List[List[int]]) -> int:
	@lru_cache(None)
	def dfs(i, j, c):
		grid[i][j] = c
		temp = 1
		for x, y in dir:
			if(0<=i+x<n and 0<=j+y<n and grid[i+x][j+y] == 1):
				temp += dfs(i+x, j+y, c)
		return temp
	n = len(grid)
	dir = [(1, 0), (-1, 0), (0, 1), (0, -1)]
	area = dict()
	c = 2
	pos = []
	for i in range(n):
		for j in range(n):
			if(grid[i][j] == 1):
				area[c] = dfs(i, j, c)
				c += 1
			elif(grid[i][j] == 0):
				pos.append((i, j))
	ans = 0
	if not(pos): return n**2
	for i, j in pos:
		neigh = set()
		for x, y in dir:
			if(0<=i+x<n and 0<=j+y<n and grid[i+x][j+y] != 0):
				neigh.add(grid[i+x][j+y])
		neigh = list(neigh)
		neigh = [area[i] for i in neigh]
		ans = max(ans, 1 + sum(neigh))
	return ans
