class Solution {
public:
    int col=2;  //color of island
    int dir[4][2]={{1,0},{0,1},{-1,0},{0,-1}};


    int dfs(vector<vector<int>>& grid,int i,int j,vector<vector<int>> &vis,vector<pair<int,int>>& c,int col)
    {
        if(i<0 || i>=grid.size() || j<0 || j>=grid[0].size() ||grid[i][j]==0||vis[i][j]!=-1)
            return 0;

        vis[i][j]=1;
        grid[i][j]=col;
        c.push_back(make_pair(i,j));
        return (1+dfs(grid,i+1,j,vis,c,col)+dfs(grid,i,j-1,vis,c,col)+dfs(grid,i,j+1,vis,c,col)+dfs(grid,i-1,j,vis,c,col));
    }

    int largestIsland(vector<vector<int>>& grid) 
    {
        int n=grid.size();
        vector<vector<int>> vis(n,vector<int>(n,-1));
        int res=1;
        unordered_map<int,int> mp;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(grid[i][j]==1 && vis[i][j]==-1)
                {
                    vector<pair<int,int>> c;
                    int ans=dfs(grid,i,j,vis,c,col);
                    res=max(res,ans);  
                    mp[col]=ans;
                    col++;
                }
            }
        }

      for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(grid[i][j]==0)
                {
                    set<int> temp;
                    if(i-1>=0)
                       temp.insert(grid[i-1][j]);
                    if(i+1<n)
                       temp.insert(grid[i+1][j]);
                    if(j-1>=0)
                       temp.insert(grid[i][j-1]);
                    if(j+1<n)
                       temp.insert(grid[i][j+1]);
                    int z=0;
                    for(auto it:temp)
                        z+=mp[it];
                    res=max(res,z+1);
                    }
                    else
                    {
                        continue;
                    }
                }
            }
            //cout<<endl;

        return res;
    }
};
