class Solution {
    int solve(int i,int j,int n,int m,vector<vector<int>>& grid, int id){

        if(i<0 || i>=n || j<0 || j>=m || grid[i][j] != 1)
            return 0;

        grid[i][j] = id;
        int ans = 1;
        ans += solve(i+1,j,n,m,grid, id);
        ans += solve(i,j+1,n,m,grid, id);
        ans += solve(i-1,j,n,m,grid, id);
        ans += solve(i,j-1,n,m,grid, id);
        return ans;
    }
public:
    int largestIsland(vector<vector<int>>& grid) {
        int ans = 0;
        int id = 2;
        unordered_map<int, int>mp;
        int n=grid.size();
        int m=grid[0].size();
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j] == 1){
                    int area = solve(i,j,n,m,grid, id);
                    ans = max(ans, area);
                    mp[id++] = area;
                }
            }
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j] == 0){
                    set<int>st;
                    if(i+1 < n && grid[i+1][j] != 0) {
                        st.insert(grid[i+1][j]);
                    }
                    if(i-1 >= 0 && grid[i-1][j] != 0) {
                        st.insert(grid[i-1][j]);
                    }
                    if(j+1 < m && grid[i][j+1] != 0) {
                        st.insert(grid[i][j+1]);
                    }
                    if(j-1 >= 0 && grid[i][j-1] != 0) {
                        st.insert(grid[i][j-1]);
                    }
                    int area = 1;
                    for(auto it : st) {
                        area+=mp[it];
                    }
                    ans = max(area, ans);
                }
            }
        }
        return ans;
    }
};
