            grid[row][col]=name
            counter+=1
            print([row, col], counter)
            for row_i, col_i in [[0,1],[1,0],[0,-1],[-1,0]]:
                if -1<row_i+row<row_n and -1<col_i+col<col_n and grid[row_i+row][col_i+col]==1:
                    dfs(row_i+row, col_i+col)

        #a for loop that makes sure we covered all islands and their sizes
        islands = dict()
        counter_max=0
        name=1   
        for row in range(row_n):
            for col in range(col_n):
                if grid[row][col]==1:
                    counter=0
                    name+=1
                    dfs(row, col)
                    islands[name]=counter
                    counter_max=max(counter_max, counter)

        #now, we investigate connecting which two or more (maximum 4) islands gives us maximum sizes
        #connecting occurs when we convert a cell of zero value to one
        for row in range(row_n):
            for col in range(col_n):
                if grid[row][col]==0:
                    counter=1
                    soundings = set()
                    for row_i, col_i in [[0,1],[1,0],[0,-1],[-1,0]]:
                        if -1<row_i+row<row_n and -1<col_i+col<col_n:
                            if islands.get(grid[row_i+row][col_i+col]):
                                soundings.add(grid[row_i+row][col_i+col]) #we keep it as a set to avoid double counting 
                    for i in soundings:
                        counter+=islands[i]
                    counter_max=max(counter_max, counter)

        return counter_max
