class Solution {
public:
    vector<int> DIR = {1, 0, -1, 0, 1};
    unordered_map<int, int> hash;
    int n, color = 2;
    int largestIsland(vector<vector<int>>& g) {
        n = g.size();
        for(int i = 0; i < n; i++)
            for(int j = 0; j < n; j++)
                if(g[i][j] == 1){
                    hash[color] = helper(g, i, j);
                    color++;
                }
        int result = -1;
        for(int i = 0; i < n; i++)
            for(int j = 0; j < n; j++)
                if(!g[i][j])
                    result = max(result, check(g, i, j));
        return result == -1 ? n * n : result + 1;
    }
    int check(vector<vector<int>>& g, int i, int j){
        unordered_set<int> set;
        for(int k = 0; k < 4; k++){
            int nr = i + DIR[k], nc = j + DIR[k + 1];
            if(nr < 0 || nc < 0 || nr == n || nc == n || !g[nr][nc])
                continue;
            set.insert(g[nr][nc]);
        }
        int count = 0;
        for(auto& a: set)
            count += hash[a];
        return count;
    }
    int helper(vector<vector<int>>& g, int i, int j){
        if(i < 0 || j < 0 || i == n || j == n || g[i][j] != 1)
            return 0;
        g[i][j] = color;
        int sum = 1;
        for(int k = 0; k < 4; k++)
            sum += helper(g, i + DIR[k], j + DIR[k + 1]);
        return sum;
    }
};
