class Solution:

    def largestIsland(self, grid: List[List[int]]) -> int:


        # Union Find Functions
        n = len(grid)
        parent = [i for i in range(0, n*n)]
        size = [1 for i in range(0, n*n)]
        def union(x,y):
            x_ref = find(x)
            y_ref = find(y)
            if x_ref != y_ref:
                parent[y_ref] = x_ref
                size[x_ref] += size[y_ref]

        def find(x):
            if x==parent[x]:
                return x
            parent[x] = find(parent[x])
            return parent[x]

        containsOne = False
        dirs = [[0,1],[1,0],[0,-1],[-1,0]]

        # Create islands using 1 Union
        for i in range(0, len(grid)):
            for j in range(0, len(grid)):
                if grid[i][j] == 0:
                    continue
                containsOne = True
                for d in dirs:
                    r = i + d[0]
                    c = j + d[1]
                    if r>=0 and c>=0 and r<n and c<n and grid[r][c] == 1:
                        union(i*n+j, r*n+c)

        # if all zero, return 1 (we can change one zero to one)
        if not containsOne:
            return 1

        max_island = max(size)
        # We can change one zero to one. Merging island in that case
        for i in range(0, n):
            for j in range(0, n):
                if grid[i][j] == 0:
                    visit = set()
                    island = 0
                    for d in dirs:
                        r = i + d[0]
                        c = j + d[1]
                        if r>=0 and c>=0 and r<n and c<n and grid[r][c] == 1:
                            par_ref = find(r*n+c)
                            if par_ref not in visit:
                                island += size[par_ref]
                                visit.add(par_ref)

                    max_island = max(island+1, max_island)

        return max_island



