class Solution {
public:

    int di[4]{-1, 1, 0, 0};
    int dj[4]{0, 0, -1, 1};

    bool isVal(int i, int j, vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        return i >= 0 && i < n && j >= 0 && j < m && grid[i][j] != 0;
    }


    int markIsland(int i, int j, vector<vector<int>>& grid, vector<vector<int>>& vis, int cpt) {
        int size = 0;
        grid[i][j] = cpt; 
        for(int k = 0; k < 4; k++) {
            int ni = i + di[k];
            int nj = j + dj[k];
            if(isVal(ni, nj, grid) && vis[ni][nj] == 0) {
                vis[ni][nj] = 1;
                size += markIsland(ni, nj, grid, vis, cpt);
            }
        }
        return 1 + size;
    }

    set<int> getIslands(int i, int j, vector<vector<int>>& grid) {
        set<int> s;
        for(int k = 0; k < 4; k++) {
            int ni = i + di[k];
            int nj = j + dj[k];
            if(isVal(ni, nj, grid)) {
               s.insert(grid[ni][nj]);
            }
        }
        return s;
    }

    int largestIsland(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();


        vector<vector<int>>vis (n,vector<int>(m,0));
        unordered_map<int, int> mp;

        int cpt = 1;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(vis[i][j] == 0 && grid[i][j] == 1) {
                    vis[i][j] = 1;
                    int size = markIsland(i, j, grid, vis, cpt);
                    mp[cpt] = size;
                    cpt++;
                }

            }
        }


        // get count of surrounding component
        int ans = 0;
        bool flag = false;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(grid[i][j] == 0) {
                    flag = true;
                    int area = 0; 
                    set<int> s = getIslands(i, j, grid);

                    for(auto it : s) {
                        int size = mp[it];
                        area += size;
                    }

                    area++;
                    ans = max(ans, area);
                }
            }
        }


        return flag ? ans : n * m;


    }
};
