class Solution {
private:
    int rSize, cSize;
    const int dir[5] = {0, 1, 0, -1, 0};
    unordered_map<int, int> sizeLookup;

    bool inBound(int r, int c) {
        return (r >= 0 && c >= 0 && r < rSize && c < cSize);
    }

    // store result in memo
    int bfs(int r, int c, int islandCnt, vector<vector<int>> &memo, vector<vector<int>> &grid) {
        queue<pair<int, int>> bfsQ;
        bfsQ.emplace(r, c);
        memo[r][c] = islandCnt;

        int size = 0;
        while (!bfsQ.empty()) {
            int currR = bfsQ.front().first;
            int currC = bfsQ.front().second;
            bfsQ.pop();
            size++;
            for (int i = 0; i < 4; i++) {
                int nextR = currR + dir[i];
                int nextC = currC + dir[i + 1];
                if (inBound(nextR, nextC) && grid[nextR][nextC] == 1 && memo[nextR][nextC] == -1) {
                    memo[nextR][nextC] = islandCnt;
