class Solution {

    int[][] dirs = new int[][]{{1, 0}, {-1, 0}, {0, 1}, {0, -1}};

    public int largestIsland(int[][] grid) {
        int N = grid.length;
        int islandId = 2;
        Map<Integer, Integer> islandIdToSizeMap = new HashMap<>();
        int biggestIslandSize = 0;

        for (int row = 0; row < N; row++) {
            for (int col = 0; col < N; col++) {
                if (grid[row][col] == 1) {
                    int islandSize = getIslandSize(row, col, grid, islandId);
                    islandIdToSizeMap.put(islandId, islandSize);
                    biggestIslandSize = Math.max(biggestIslandSize, islandSize);
                    islandId++;
                }
            }
        }

        for (int row = 0; row < N; row++) {
            for (int col = 0; col < N; col++) {
                if (grid[row][col] == 0) {
                    Set<Integer> seenIslands = new HashSet<>();
                    int sumAdjacentIslandsSize = 0;
                    for (int[] dir : dirs) {
                        int nextRow = row + dir[0];
                        int newCol = col + dir[1];
                        if (nextRow >= 0 && nextRow < N && newCol >= 0 && newCol < N && grid[nextRow][newCol] != 0 && !seenIslands.contains(grid[nextRow][newCol])) {
                            sumAdjacentIslandsSize += islandIdToSizeMap.get(grid[nextRow][newCol]);
                            seenIslands.add(grid[nextRow][newCol]);
                        }
                    }
                    biggestIslandSize = Math.max(biggestIslandSize, sumAdjacentIslandsSize + 1);
                }
            }
        }

        return biggestIslandSize;
    }


    private int getIslandSize(int row, int col, int[][] grid, int islandId) {
        Queue<int[]> q = new LinkedList<>();
        grid[row][col] = islandId;
        q.offer(new int[]{row, col});

        int islandSize = 0;
        while (!q.isEmpty()) {
            islandSize++;
            int[] curr = q.poll();
            row = curr[0];
            col = curr[1];
            for (int[] dir : dirs) {
                int nextRow = row + dir[0];
                int nextCol = col + dir[1];
                if (nextRow >= 0 && nextRow < grid.length && nextCol >= 0 && nextCol < grid.length && grid[nextRow][nextCol] == 1) {
                    q.offer(new int[]{nextRow, nextCol});
                    grid[nextRow][nextCol] = islandId;
                }
            }
        }

        return islandSize;
    }
}
