}
class Solution {

    public boolean isValid(Pair temp,int n,int m){
        if(temp.x>=n || temp.x<0 || temp.y>=m || temp.y<0)
            return false;
        return true;
    }
    public int largestIsland(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int visited[][] = new int[n][m];
        int X[] = {0,0,1,-1};
        int Y[] = {1,-1,0,0};
        int groupID = 0;
        int maxGroupSize[] = new int [n*m+1];
        int maxSize = 0;
        int ans  =0;
        for(int i=0;i<n;i++) {
            for(int j=0;j<m;j++){
                if(visited[i][j] > 0 || grid[i][j] == 0) continue;
                Queue<Pair> q = new LinkedList<>();
                q.add(new Pair(i,j));
                groupID++;
                visited[i][j]  = groupID;
                int size =0;
                while(q.size()!=0) {
                    Pair current = q.peek();
                    q.remove();
                    size++;
                    for(int k=0;k<4;k++){
                        Pair temp = new Pair(current.x+ X[k],current.y+Y[k]);
                            if(isValid(temp,n,m) && visited[temp.x][temp.y] ==0 && grid[temp.x][temp.y] == 1) {
                                q.add(temp);
                                visited[temp.x][temp.y]  = groupID;
                            }
                    }
                }
                maxGroupSize[groupID] = size;  
                ans = Math.max(ans,size);

            }
        }
        // System.out.println(Arrays.toString(maxGroupSize));

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j] == 1) continue;
                // Pair current = new Pair(i,j);
                int arrT[] = new int[4];
                int t=0;
                int tempAns =1;
                for(int k=0;k<4;k++){
                     Pair temp = new Pair(i+X[k],j+Y[k]);
                    if(isValid(temp,n,m) && grid[temp.x][temp.y] == 1) {
                        arrT[t] = visited[temp.x][temp.y];
                        t++;
                    }
                }
                Arrays.sort(arrT);
                tempAns +=maxGroupSize[arrT[0]];
                for(int k =1;k<4;k++){
                    if(arrT[k-1] != arrT[k]) tempAns+=maxGroupSize[arrT[k]];
                }
                ans = Math.max(tempAns,ans);                
            }
        }        
       return ans;              
    }
}
