class Solution {
    private int [][]dirs = {
        {0, 1},
        {0, -1},
        {1, 0},
        {-1, 0}
    };

    public int largestIsland(int[][] grid) {
        if(grid == null || grid.length == 0){
            return 0;
        }

        Map<Integer, Integer> islandMap = new HashMap<>();
        int key = 2;
        int ans =  0;

        //calculate area of each island and name them with unique key
        for(int i=0; i<grid.length; i++){
            for(int j=0; j<grid[0].length; j++){
                if(grid[i][j] == 1){
                    int area = dfs(grid, i, j, key);
                    ans = Math.max(area, ans);
                    islandMap.put(key,area);
                    key++;
                }
            }
        }


        // now check every possible 0 and its neighbor islands
        for(int i=0; i<grid.length; i++){
            for(int j=0; j<grid[0].length; j++){
                if(grid[i][j] == 0){
                    ans = Math.max(ans, checkNeighborIslands(grid, i, j, islandMap));
                }
            }
        }

        return ans;
    }

    private int checkNeighborIslands(int [][]grid, int r, int c, Map<Integer, Integer> islandMap){
        int neighborIslandArea = 0;
        Set<Integer> seen = new HashSet<>();

        for(int []dir : dirs){
            int nr = dir[0] + r;
            int nc = dir[1] + c;

            if(nr >= 0 && nc >= 0 && nr < grid.length && nc < grid[0].length && islandMap.containsKey(grid[nr][nc]) && !seen.contains(grid[nr][nc])){
                neighborIslandArea += islandMap.get(grid[nr][nc]);
                seen.add(grid[nr][nc]);
            }
        }

        return neighborIslandArea + 1;
    }


    private int dfs(int [][]grid, int r, int c, int key){
        if(r >= 0 && c >= 0 && r < grid.length && c < grid[0].length && grid[r][c] == 1){
            grid[r][c] = key;

            int ans = 1;
            for(int []dir : dirs){
                int nr = r + dir[0];
                int nc = c + dir[1];

              ans += dfs(grid, nr, nc, key);
            }

            return ans;
        }

        return 0;
    }

}
