            if (isValid(u, v) && grid[u][v] && visited[u][v] == false) {
                cnt += dfs(u, v, uf, par, grid);
            }
        }
        return cnt;
    }

    int largestIsland(vector<vector<int>>& grid) {
        unordered_map<int, int> mp;
        n = grid.size();
        m = grid[0].size();
		//we are going to represent each node in a single array
        unionFind uf(n * m);
        for (int i = 0; i < n; ++i) for (int j = 0; j < m; ++j) visited[i][j] = false;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (visited[i][j] == false && grid[i][j]) {
				    //Get the number of nodes connected in this island
                    int cnt = dfs(i, j, uf, i * m + j, grid);
                    mp[i * m + j] = cnt;
                }
            }
        }

        int ans = 0;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j] == 0) {
                    int curr = 0;
                    int d[] = {-1, 0, 1, 0, -1};
                    set<int> s;
                    for (int k = 0; k < 4; ++k) {
                        int u = i + d[k];
                        int v = j + d[k + 1];
                        if (isValid(u, v)) {
						    //get the parent of the current node and where this node belongs
						    //we will be using current node to merge multiple islands into a single big island
                            int par = uf.find(u * m + v);
                            s.insert(par);
                        }
                    }
					for (auto a: s) {
						curr += mp[a];
					}
                    ans = max(ans, curr + 1);
                }
            }
        }
		//If the answer remains 0, that means, there was no zero in the matrix.
        return (ans == 0? n * m: ans);
    }
};
